package com.wk;import java.io.File;import java.io.FilenameFilter;//按指定扩展名进行过滤，定义一个类实现FilenameFilter接口class NameFilter implements FilenameFilter {    //私有域扩展名    private String extent;    //接收指定的扩展名    public NameFilter(String extent) {        this.extent = extent;    }    @Override    public boolean accept(File dir, String name) {        return name.endsWith(extent);        //如果文件符合指定的扩展名则返回true，否则返回false    }}