package com.wk;import com.alibaba.fastjson.JSONArray;import com.alibaba.fastjson.JSONObject;import org.apache.tomcat.util.http.fileupload.FileUtils;import org.springframework.core.io.ClassPathResource;import java.io.*;import java.util.HashMap;import java.util.Iterator;public class ReadUtil {    /**     * 读取json文件，返回json串     * @param fileName     * @return     */    public static String readJsonFile(String fileName) {        String jsonStr = "";        try {            File jsonFile = new File(fileName);            FileReader fileReader = new FileReader(jsonFile);            Reader reader = new InputStreamReader(new FileInputStream(jsonFile),"utf-8");            int ch = 0;            StringBuffer sb = new StringBuffer();            while ((ch = reader.read()) != -1) {                sb.append((char) ch);            }            fileReader.close();            reader.close();            jsonStr = sb.toString();            return jsonStr;        } catch (IOException e) {            e.printStackTrace();            return null;        }    }}